('642', 'GEOG', '001376', 'GRD', 'Micrometeorology', 'Advanced study of boundary-layer meteorology. Energy balance regimes for complex surfaces are examined. The theory of turbulent and radiant transfer is studied using field data collected during the course. *eligible for MES.', NULL, 'No Consent Required', 'No Consent Required', NULL),
('643', 'GEOG', '001377', 'GRD', 'Dynamic Geomorphology', 'An overview of current issues and methodologies in geomorphology with special emphasis on processes operating in fluvial, glacial, periglacial or coastal environments. *eligible for MES.', NULL, 'No Consent Required', 'No Consent Required', NULL),
